def loadData():
    with open('data.txt', 'r') as f:
        for i, line in enumerate(f):
            line = line.replace('\n', '')

def takeStairs(data):
    floor = 0

    for c in data:
        if c == '(':
            floor = floor + 1
        else:
            floor = floor - 1
    print(f'Result Floor: {floor}')

def firstTimeDownstairs(data):
    floor = 0
    ctr = 0

    for c in data:
        ctr = ctr + 1
        if c == '(':
            floor = floor + 1
        else:
            floor = floor - 1

        if floor < 0:
            break
    print(f'First Time Downstairs: {ctr}')

