data = ''


def detectMarkerStartpacket():
    index = 0
    for c in data:
        if not findDuplicateLetters(data[index:(index + 4)]):
            print(f'First start-of-packet marker {data[index:(index + 4)]} is found after {index + 4} Characters')
            break
        index = index + 1

def detectMarkerMessage():
    index = 0
    for c in data:
        if not findDuplicateLetters(data[index:(index + 14)]):
            print(f'First start-of-message marker {data[index:(index + 14)]} is found after {index + 14} Characters')
            break
        index = index + 1

def findDuplicateLetters(str):
    for c in str:
        letterOccurance = 0
        for c2 in str:
            if c == c2:
                letterOccurance= letterOccurance + 1
        if letterOccurance > 1:
            return True;
    return False;

if __name__ == '__main__':
    # Part 1
    detectMarkerStartpacket()

    # Part 2
    detectMarkerMessage()
